/* Проверка поддержки webp, добавление класса webp или no-webp для HTML */
function isWebp() {
	// Проверка поддержки webp
	function testWebP(callback) {
		let webP = new Image();
		webP.onload = webP.onerror = function () {
			callback(webP.height == 2);
		};
		webP.src = "data:image/webp;base64,UklGRjoAAABXRUJQVlA4IC4AAACyAgCdASoCAAIALmk0mk0iIiIiIgBoSygABc6WWgAA/veff/0PP8bA//LwYAAA";
	}
	// Добавление класса _webp или _no-webp для HTML
	testWebP(function (support) {
		let className = support === true ? 'webp' : 'no-webp';
		document.documentElement.classList.add(className);
	});
}
isWebp()


/* Slider */
let btn_prev = document.querySelector('.icon-arrow-prev');
let btn_next = document.querySelector('.icon-arrow-next');
let slides = document.querySelectorAll('.review__slider-item');
let i = 0;
let timeout = 5000;
let order = [1, 2, 3];

let slider = setInterval(() => {
	order.unshift(order.pop());
	order.forEach((v, i) => {
		slides[i].removeAttribute('class')
		slides[i].classList.add("slide-" + v, "review__slider-item")
	});
	i++;
	if (i >= slides.length) i = 0;
}, timeout);

btn_prev.onclick = function () {
	order.push(order.shift());
	order.forEach((v, i) => {
		slides[i].removeAttribute('class')
		slides[i].classList.add("slide-" + v, "review__slider-item")
	});
	i--;
	if (i < 0) i = slides.length - 1;
}

btn_next.onclick = function () {
	order.unshift(order.pop());
	order.forEach((v, i) => {
		slides[i].removeAttribute('class')
		slides[i].classList.add("slide-" + v, "review__slider-item")
	});
	i++;
	if (i >= slides.length) i = 0;
}